import React, { useEffect, useState } from "react";
import './login.css'
import { Link } from "react-router-dom";

function Login(){
    const [logininfo, setlogininfo] = useState({
        username:"",
        password:"",
    })

 
    const postData = async () => {
        
        try {
            const response = await fetch(`http://localhost:8080/login/${logininfo.username}/${logininfo.password}`, {
                method: 'GET' 
            });
            if (response.ok) {
                const data = await response.json();
                console.log(data);
            } else {
                console.error('Error:', response.status, response.statusText);
            }
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

       

    const handleChange = (e) => {
        const { name, value } = e.target;
        setlogininfo(prevUserInfo => ({
            ...prevUserInfo,
            [name]: value
        }));
    };

    const handleLogin =()=>{
        postData()
        
    }

    return(
        <div className="d-flex  justify-content-center container">
        <div className='cardform m-3 p-2 row row'>
            <form> 
                <div>
                <label htmlFor="username">Username</label>
                    <input type='email' id="username"  name="username" onChange={handleChange} value={logininfo.username}/>
                    <label htmlFor="password">Password</label>
                    <input type='password' id="password"  name="password" onChange={handleChange} value={logininfo.password}/>
                    <Link to="forgetpassword">
                    <p className="text-center">Forget password</p>
                    </Link>
                    <div className="text-center">
                    <Link to="google-login">
                        <img  src="https://cdn1.iconfinder.com/data/icons/google-s-logo/150/Google_Icons-09-512.png" className="google-logo"/>
                    </Link>

                    </div>
                    
                </div>
               
            </form>
            <div>
        <button className="btn btn-primary" onClick={handleLogin}>Login</button>
        </div>
        <Link to="/">
            <button className="btn btn-primary mt-2">Back</button>
        </Link>
        
        </div>
        
        
        </div>
            
        
    )
}

export default Login
