import React, { useEffect, useState } from "react";
import './login.css'
import { Link } from "react-router-dom";

function Login(){
    const [logininfo, setlogininfo] = useState({
        username:"",
        password:"",
    })

 
    const postData = async () => {
        
        try {
            const response = await fetch(`http://localhost:8080/login/${logininfo.username}/${logininfo.password}`, {
                method: 'GET' 
            });
            if (response.ok) {
                const data = await response.json();
                console.log(data);
            } else {
                console.error('Error:', response.status, response.statusText);
            }
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

       

    const handleChange = (e) => {
        const { name, value } = e.target;
        setlogininfo(prevUserInfo => ({
            ...prevUserInfo,
            [name]: value
        }));
    };

    const handleLogin =()=>{
        postData()
        
    }

    return(
        <div className="bg-login">
        <div className="cardform">
        <div className='form'>
            <form> 
                <div className="form-container">
                <label htmlFor="username">Username</label>
                    <input type='email' id="username" placeholder='Username' name="username" className="input-ele" onChange={handleChange} value={logininfo.username}/>
                    <label htmlFor="password">Password</label>
                    <input type='password' id="password" placeholder='Password' name="password" className="input-ele" onChange={handleChange} value={logininfo.password}/>
                    <Link to="forgetpassword">
                    <p className="text-center">Forget password</p>
                    </Link>
                    <div className="text-center">
                    <Link to="google-login">
                        <img  src="https://cdn1.iconfinder.com/data/icons/google-s-logo/150/Google_Icons-09-512.png" className="google-logo"/>
                    </Link>

                    </div>
                    
                </div>
               
            </form>
        </div>
        <div className="text-center">
        <button className="button-class" onClick={handleLogin}>Login</button>
        </div>

        
        </div>
    </div>
            
        
    )
}

export default Login
