import React, { useEffect, useState } from "react";
import './login.css'
import { Link } from "react-router-dom";

function Login(){
    const [logininfo, setlogininfo] = useState({
        username:"",
        password:"",
    })

 
    const postData = async () => {
        
        try {
            const response = await fetch(`http://localhost:8080/login/${logininfo.username}/${logininfo.password}`, {
                method: 'GET' 
            });
            if (response.ok) {
                const data = await response.json();
                console.log(data);
            } else {
                console.error('Error:', response.status, response.statusText);
            }
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

       

    const handleChange = (e) => {
        const { name, value } = e.target;
        setlogininfo(prevUserInfo => ({
            ...prevUserInfo,
            [name]: value
        }));
    };

    const handleLogin =()=>{
        postData()
        
    }

    return(
        <div className="d-flex  justify-content-center container">
        <div className='login-cardform m-3 p-2 row row'>
            
        <div className='col-12'>
            <form className="form-div"> 
                <div className="first-form">
                    <label htmlFor="username">Username</label>
                    <input type='email' id="username"  name="userName" className="input-ele" onChange={handleChange} value={userinfo.userName}/>
                    <label htmlFor="password">Password</label>
                    <input type='password' id="password"  name="password" className="input-ele" onChange={handleChange} value={userinfo.password}/>
                    
                </div>
               
            </form>
        </div>
            <div>
        <button className="btn btn-primary" onClick={handleLogin}>Login</button>
        </div>
        <Link to="/">
            <button className="btn btn-primary mt-2">Back</button>
        </Link>
        
        </div>
        
        
        </div>
            
        
    )
}

export default Login
